import { Component  } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';

@Component({
	selector: 'app-read-file',
	templateUrl: './read-file.component.html',
	styleUrls: ['./read-file.component.css']
})

export class ReadFileComponent {

	gedPattern = "^[a-zA-Z]$";
	projectNamePattern = "";

	fileForm = new FormGroup({
	    filePath: new FormControl('', Validators.required),
	    projectName: new FormControl('', [Validators.required, Validators.minLength(3), Validators.maxLength(16)]),	    
	  });

	constructor() { } 

	fileImputChangeValue() {
		console.log("CHANGE " + this.fileForm.value);
	}

	onSubmit(){
		console.log('Name: ' + this.fileForm.value);
		console.log('Validation Status: ' + this.fileForm.status);
	}
}
